// Imagem PNG Real (Bordô com DCL) convertida em Base64
const ICON_BASE64 = '';

function getIconBlob(base64) {
  const binary = atob(base64);
  const array = new Uint8Array(binary.length);
  for (let i = 0; i < binary.length; i++) array[i] = binary.charCodeAt(i);
  return new Blob([array], { type: 'image/png' });
}

self.addEventListener('fetch', (event) => {
  // Se o Windows pedir o ícone, o SW entrega o PNG gerado na hora
  if (event.request.url.includes('icon-512.png')) {
    event.respondWith(
      new Response(getIconBlob(ICON_BASE64), {
        headers: { 'Content-Type': 'image/png' }
      })
    );
  } else {
    event.respondWith(fetch(event.request).catch(() => new Response("offline")));
  }
});

self.addEventListener('install', () => self.skipWaiting());
self.addEventListener('activate', (e) => e.waitUntil(clients.claim()));
